    private void setvalues(){
        ob.setEmp_id(Integer.parseInt(s_empid.getText()));
        ob.setAmount(Integer.parseInt(s_amount.getText()));
        ob.setPrice(Integer.parseInt(s_price.getText())); 
        ob.setBrand(s_brand.getText());
      ob.setClient_email(s_clientemail.getText());
       ob.setClient_phone(s_clientphone.getText());
       ob.setEmp_name(s_empname.getText());
       ob.setColor(s_color.getText());
     ob.setClient_name(s_clientname.getText());
       ob.setDate(s_date.getText());
        
        
    }
