    private void s1_tableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_s1_tableMouseClicked
       try{
            int row=s1_table.getSelectedRow();
            String sname=s1_table.getValueAt(row, 0).toString();
             String sphone=s1_table.getValueAt(row, 1).toString();
               String semail=s1_table.getValueAt(row,2).toString();
            String sid=s1_table.getValueAt(row, 3).toString();
           String sename=s1_table.getValueAt(row, 4).toString();
            String samount=s1_table.getValueAt(row, 5).toString();
           String sbrand=s1_table.getValueAt(row, 6).toString();
         String scolor=s1_table.getValueAt(row, 7).toString();
           String sprice=s1_table.getValueAt(row, 8).toString();
           String sdate=s1_table.getValueAt(row, 9).toString();


            s_amount.setText(samount);
            s_brand.setText(sbrand);
            s_clientemail.setText(semail);
            s_clientname.setText(sname);
            s_clientphone.setText(sphone);
            s_color.setText(scolor);
            s_date.setText(sdate);
            s_empid.setText(sid);
            s_empname.setText(sename);
            s_price.setText(sprice);
            

        }
        catch(Exception ex){
           Tools.msgBox(ex.getMessage()); 
        }
    }//GEN-LAST:event_s1_tableMouseClicked


