    private void setvalues(){
       b.setId(Integer.parseInt(s1_id.getText()));
        b.setName(s1_name.getText());
        b.setPhone(s1_phone.getText());
        b.setEmail(s1_email.getText());
        b.setAddress(s1_address.getText());
         b.setCompany(s1_company.getText());
    }
    sup ob=new sup();
      private void ClearData(){
        Tools.clearText(this);
        s1_id.setText(b.getAutoNumber()); 
       s1_name.requestFocus(); 
    }
      
