    private void s_searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_s_searchActionPerformed
        // TODO add your handling code here:
        
           String sreachkey="select * from suptest1 where ";
        if(sid.isSelected()){
            sreachkey+="number";
        }
        else{
            sreachkey+="name";
        }
        sreachkey+=" like'%"+s1_search.getText()+"%'";
        b.getCustomRows(sreachkey, s1_table);
    }//GEN-LAST:event_s_searchActionPerformed

