    private void s1_tableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_s1_tableMouseClicked
        // TODO add your handling code here:
        
          try{
            int row=s1_table.getSelectedRow();
            String sid=s1_table.getValueAt(row, 0).toString();
             String sname=s1_table.getValueAt(row, 1).toString();
           String sphone=s1_table.getValueAt(row, 2).toString();
           String semail=s1_table.getValueAt(row, 3).toString();
            String saddress=s1_table.getValueAt(row, 4).toString();
               String scompany=s1_table.getValueAt(row,5).toString();
            s1_id.setText(sid);
           s1_name.setText(sname);
            s1_phone.setText(sphone);
            s1_email.setText(semail);
            s1_address.setText(saddress);
           s1_company.setText(scompany);  
        }
        catch(Exception ex){
           Tools.msgBox(ex.getMessage()); 
        }
    }//GEN-LAST:event_s1_tableMouseClicked

